// << , >>
/*
 * 		10 << 2
 * 		1010 00
 * 
 * 		x<<y : x*2^y
 * 		x>>y : x/2^y
 */
public class 쉬프트연산자 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println(10<<2);
		System.out.println(7<<3);
		System.out.println(8<<2);
		
		System.out.println(15>>3);
		System.out.println(10>>2);



	}

}
